import { useState } from "react";

// Utility to calculate contrast ratio
function luminance(r, g, b) {
  const a = [r, g, b].map((v) => {
    v /= 255;
    return v <= 0.03928 ? v / 12.92 : Math.pow((v + 0.055) / 1.055, 2.4);
  });
  return a[0] * 0.2126 + a[1] * 0.7152 + a[2] * 0.0722;
}

function contrast(hex1, hex2) {
  const c1 = hex1.substring(1).match(/.{2}/g).map((x) => parseInt(x, 16));
  const c2 = hex2.substring(1).match(/.{2}/g).map((x) => parseInt(x, 16));
  const L1 = luminance(c1[0], c1[1], c1[2]);
  const L2 = luminance(c2[0], c2[1], c2[2]);
  return (Math.max(L1, L2) + 0.05) / (Math.min(L1, L2) + 0.05);
}

export default function App() {
  const [messages, setMessages] = useState([
    { from: "bot", text: "Hi! ðŸ‘‹ I'm your chatbot. How can I help you today?" },
  ]);
  const [input, setInput] = useState("");

  // Style configuration state
  const [config, setConfig] = useState({
    darkMode: false,
    showPoweredBy: true,
    profilePic: "https://via.placeholder.com/40",
    chatIcon: "ðŸ’¬",
    userBubble: "#4F46E5",
    botBubble: "#E5E7EB",
    userText: "#ffffff",
    botText: "#111827",
    headerBg: "#4F46E5",
    areaBg: "#F9FAFB",
    bubbleRadius: "12px",
    fontSize: "14px",
    fontFamily: "sans-serif",
    widgetWidth: "360px",
    cornerRadius: "16px",
    syncHeader: false,
  });

  const handleSend = () => {
    if (!input.trim()) return;
    setMessages((prev) => [...prev, { from: "user", text: input }]);
    setTimeout(() => {
      setMessages((prev) => [...prev, { from: "bot", text: "Echo: " + input }]);
    }, 800);
    setInput("");
  };

  // Contrast warnings
  const warnings = [];
  if (contrast(config.userBubble, config.userText) < 4.5) {
    warnings.push("âš ï¸ Low contrast: User bubble & text may be hard to read.");
  }
  if (contrast(config.botBubble, config.botText) < 4.5) {
    warnings.push("âš ï¸ Low contrast: Bot bubble & text may be hard to read.");
  }
  if (contrast(config.headerBg, config.userText) < 4.5) {
    warnings.push("âš ï¸ Low contrast: Header background & text may be hard to read.");
  }

  return (
    <div
      className={`min-h-screen flex flex-col md:flex-row ${
        config.darkMode ? "bg-gray-900 text-white" : "bg-gray-100 text-gray-900"
      }`}
    >
      {/* Chat Widget */}
      <div
        className="flex flex-col m-4 shadow-lg overflow-hidden"
        style={{
          width: config.widgetWidth,
          borderRadius: config.cornerRadius,
          fontSize: config.fontSize,
          fontFamily: config.fontFamily,
          background: config.areaBg,
        }}
      >
        {/* Header */}
        <div
          className="flex items-center gap-2 p-3 font-semibold"
          style={{
            background: config.headerBg,
            color: config.userText,
          }}
        >
          <img
            src={config.profilePic}
            alt="agent"
            className="w-8 h-8 rounded-full"
          />
          <span>{config.chatIcon}</span>
          <span>Chatbot Assistant</span>
        </div>

        {/* Messages */}
        <div className="flex-1 overflow-y-auto p-3 space-y-2">
          {messages.map((msg, idx) => (
            <div
              key={idx}
              className={`flex ${
                msg.from === "user" ? "justify-end" : "justify-start"
              }`}
            >
              <div
                className="px-3 py-2 max-w-[70%]"
                style={{
                  borderRadius: config.bubbleRadius,
                  background:
                    msg.from === "user" ? config.userBubble : config.botBubble,
                  color: msg.from === "user" ? config.userText : config.botText,
                }}
              >
                {msg.text}
              </div>
            </div>
          ))}
        </div>

        {/* Input */}
        <div className="p-3 flex items-center gap-2 border-t">
          <input
            type="text"
            placeholder="Type a message..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && handleSend()}
            className="flex-1 p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
          />
          <button
            onClick={handleSend}
            className="bg-indigo-600 hover:bg-indigo-700 text-white px-3 py-2 rounded-lg"
          >
            Send
          </button>
        </div>

        {/* Powered by */}
        {config.showPoweredBy && (
          <div className="text-xs text-center p-1">
            Powered by ChatbotUI âš¡
          </div>
        )}
      </div>

      {/* Style Panel */}
      <div className="flex-1 p-4 space-y-4">
        <h2 className="text-lg font-bold">ðŸŽ¨ Style Config</h2>

        {/* Contrast Warnings */}
        {warnings.length > 0 && (
          <div className="p-2 bg-yellow-100 border border-yellow-400 text-yellow-800 rounded">
            {warnings.map((w, i) => (
              <div key={i}>{w}</div>
            ))}
          </div>
        )}

        {/* Dark Mode */}
        <label className="flex items-center gap-2">
          <input
            type="checkbox"
            checked={config.darkMode}
            onChange={(e) =>
              setConfig({ ...config, darkMode: e.target.checked })
            }
          />
          Dark Mode
        </label>

        {/* Powered By */}
        <label className="flex items-center gap-2">
          <input
            type="checkbox"
            checked={config.showPoweredBy}
            onChange={(e) =>
              setConfig({ ...config, showPoweredBy: e.target.checked })
            }
          />
          Show "Powered by"
        </label>

        {/* Sync user bubble with header */}
        <label className="flex items-center gap-2">
          <input
            type="checkbox"
            checked={config.syncHeader}
            onChange={(e) => {
              const sync = e.target.checked;
              setConfig((prev) => ({
                ...prev,
                syncHeader: sync,
                userBubble: sync ? prev.headerBg : prev.userBubble,
              }));
            }}
          />
          Sync user bubble color with header
        </label>

        {/* Profile Pic */}
        <div>
          <label className="block font-medium">Profile Picture URL</label>
          <input
            type="text"
            value={config.profilePic}
            onChange={(e) => setConfig({ ...config, profilePic: e.target.value })}
            className="w-full p-1 border rounded"
          />
        </div>

        {/* Chat Icon */}
        <div>
          <label className="block font-medium">Chat Icon</label>
          <input
            type="text"
            value={config.chatIcon}
            onChange={(e) => setConfig({ ...config, chatIcon: e.target.value })}
            className="w-full p-1 border rounded"
          />
        </div>

        {/* Colors */}
        <div className="grid grid-cols-2 gap-3">
          <div>
            <label>User Bubble</label>
            <input
              type="color"
              value={config.userBubble}
              onChange={(e) => {
                if (!config.syncHeader) {
                  setConfig({ ...config, userBubble: e.target.value });
                }
              }}
              className="w-full h-10"
              disabled={config.syncHeader}
            />
          </div>
          <div>
            <label>Bot Bubble</label>
            <input
              type="color"
              value={config.botBubble}
              onChange={(e) =>
                setConfig({ ...config, botBubble: e.target.value })
              }
              className="w-full h-10"
            />
          </div>
          <div>
            <label>User Text</label>
            <input
              type="color"
              value={config.userText}
              onChange={(e) =>
                setConfig({ ...config, userText: e.target.value })
              }
              className="w-full h-10"
            />
          </div>
          <div>
            <label>Bot Text</label>
            <input
              type="color"
              value={config.botText}
              onChange={(e) =>
                setConfig({ ...config, botText: e.target.value })
              }
              className="w-full h-10"
            />
          </div>
          <div>
            <label>Header BG</label>
            <input
              type="color"
              value={config.headerBg}
              onChange={(e) => {
                setConfig((prev) => ({
                  ...prev,
                  headerBg: e.target.value,
                  userBubble: prev.syncHeader ? e.target.value : prev.userBubble,
                }));
              }}
              className="w-full h-10"
            />
          </div>
          <div>
            <label>Area BG</label>
            <input
              type="color"
              value={config.areaBg}
              onChange={(e) =>
                setConfig({ ...config, areaBg: e.target.value })
              }
              className="w-full h-10"
            />
          </div>
        </div>

        {/* Bubble Radius */}
        <div>
          <label>Bubble Radius ({config.bubbleRadius})</label>
          <input
            type="range"
            min="0"
            max="24"
            value={parseInt(config.bubbleRadius)}
            onChange={(e) =>
              setConfig({ ...config, bubbleRadius: e.target.value + "px" })
            }
            className="w-full"
          />
        </div>

        {/* Font Size */}
        <div>
          <label>Font Size ({config.fontSize})</label>
          <input
            type="range"
            min="12"
            max="18"
            value={parseInt(config.fontSize)}
            onChange={(e) =>
              setConfig({ ...config, fontSize: e.target.value + "px" })
            }
            className="w-full"
          />
        </div>

        {/* Font Family */}
        <div>
          <label>Font Family</label>
          <select
            value={config.fontFamily}
            onChange={(e) =>
              setConfig({ ...config, fontFamily: e.target.value })
            }
            className="w-full p-1 border rounded"
          >
            <option value="sans-serif">Sans-serif</option>
            <option value="serif">Serif</option>
            <option value="monospace">Monospace</option>
          </select>
        </div>

        {/* Widget Width */}
        <div>
          <label>Widget Width ({config.widgetWidth})</label>
          <input
            type="range"
            min="280"
            max="420"
            value={parseInt(config.widgetWidth)}
            onChange={(e) =>
              setConfig({ ...config, widgetWidth: e.target.value + "px" })
            }
            className="w-full"
          />
        </div>

        {/* Corner Radius */}
        <div>
          <label>Corner Radius ({config.cornerRadius})</label>
          <input
            type="range"
            min="0"
            max="24"
            value={parseInt(config.cornerRadius)}
            onChange={(e) =>
              setConfig({ ...config, cornerRadius: e.target.value + "px" })
            }
            className="w-full"
          />
        </div>
      </div>
    </div>
  );
}
